import axios from "axios";
import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const CreateFavorite = (props) => {
    const [web_name, setWeb_name] = useState("");
    const [url, setUrl] = useState("");
    const [description, setDescription] = useState("");
    const [errors, setErrors] = useState([]);
    const navigate = useNavigate();
    
    const handleSubmit = (e) => {
        e.preventDefault();
        axios
        .post("http://localhost:8000/api/favorites", { 
            web_name,
            url,
            description
        })
        .then((response) => {
        console.log(response);
        console.log(response.data);
        navigate("/display");
        })
        .catch((err) => {
            console.log(err.response.data.err.errors);
            setErrors(err.response.data.err.errors);
        });
    };
    
    return (
        <div class="container">
            <h4>Create a Favorite: </h4>
            <form id="outline" onSubmit={handleSubmit}>
                <div class="row">
                    <div class="col">
                        <div class="form-group">
                            <label htmlFor="web_name">Website Name: </label>
                            <input class="form-control" type="text" id="web_name" value={web_name}
                            onChange={(e) => setWeb_name(e.target.value)}/>
                            {errors.web_name ? <p>{errors.web_name.message}</p> : null}
                        </div>
                    </div>
                    <div class="col">
                        <div class="form-group">
                            <label htmlFor="url">Website URL: </label>
                            <input class="form-control" type="text" id="url" value={url}
                            onChange={(e) => setUrl(e.target.value)}/>
                            {errors.url ? <p>{errors.url.message}</p> : null}
                        </div>
                    </div>
                    <div class="col">
                        <div class="form-group">
                            <label htmlFor="description">Description: </label>
                            <input class="form-control" type="text" id="description" value={description}
                            onChange={(e) => setDescription(e.target.value)}/>
                            {errors.description ? <p>{errors.description.message}</p> : null}
                        </div>
                    </div>
                </div>                
                <div class="col">
                <button class="btn btn-primary" type="submit" > Add Favorite </button>
                </div>
                <br></br>
            </form>
        </div>
    );
};

export default CreateFavorite;
