import React, { useEffect, useState } from "react";
import axios from "axios";
import "../App.css";
import {useNavigate, useParams, Link} from 'react-router-dom';

const DisplaySearch = () => {
    var title = window.sessionStorage.getItem('result');
    var x = sessionStorage.getItem('title');
    const { url } = useParams();
    const [web_name, setWeb_name] = useState("");
    const [description, setDescription] = useState("");
    const navigate = useNavigate();
    const [search, setSearch] = useState([]);
    const [errors, setErrors] = useState([]);
    const [searchNotFoundError, setSearchNotFoundError] = useState("");
    
    useEffect(() => {
        axios.get(`http://api.linkpreview.net/?key=bfba97d82a17454fd66fd07e5ef5d4aa&q=${url}`)
            .then((response) => {
                console.log(response.data);
                setSearch(response.data);
            })
            .catch((err) => {
                console.log(err.response);
                setSearchNotFoundError(`Search not found using that URL`);
            });
    }, [url]);

    const handleSubmit = (e) => {
        e.preventDefault();
        axios
        .post("http://localhost:8000/api/favorites", { 
            web_name: search.title,
            url: search.url,
            description: search.description
        })
        .then((response) => {
        console.log(response);
        console.log(response.data);
        navigate("/display");
        })
        .catch((err) => {
            console.log(err.response.data.err.errors);
            setErrors(err.response.data.err.errors);
        });
    };


    return (
        <div>
            <div>
            <h4>{search.title}</h4>
            {title}
            
            {errors.title ? <p>{errors.title.message}</p> : null}
            </div>
            <div id="outline">
                <br></br>
                <div>
                    <div>
                        <div>
                            <strong>URL: </strong>  {x}
                        </div>
                    </div>
                </div>
                <br></br>
                <div>
                    <div>
                        <div>
                            <strong>Description: </strong>  {search.description}
                        </div>
                    </div>
                </div>
                <br></br>
            </div>
            <button id="favorite" class="btn btn-primary" onClick={handleSubmit}> Favorite {search.title} </button>
        </div>
    );
};

export default DisplaySearch;
